import { reqGetCode, reqUserLogin } from "@/api";

const actions = {
    //获取验证码
    async getCode({ commit }, email) {
        let result = await reqGetCode(email);
        if(result.code == 200){
            commit("GETCODE", result.data)
            return 'ok'
        }else {
            return Promise.reject(new Error('faile'))
        }

    },
    //登录业务
    async userLogin({ commit }, data) {
        console.log("aaaaaaaa", data);
        let result = await reqUserLogin(data);
        console.log(result);
    },
};
const mutations = {
    GETCODE(state,code){
        state.code = code;
    }
};
const state = {
    code: ""
};
const getters = {};
export default {
    actions,
    mutations,
    state,
    getters,
};
